import styled from 'styled-components'

export const ButtonFavoriteStyled = styled.button`
	position: absolute;
	right: 15px;
	top: 15px;
	border: white;
	stroke: white;
	background-color: transparent;
	font-size: 24px;
	cursor: pointer;
	color: rgba(255, 255, 255, 0.8);
	&:hover  {
		transform: rotate(45deg);
	}

`
